function [v,y,steps]=pm(A)% power method (general version)% v=output eigenvalue  y=output eigenvectorn=length(A(:,1));% guess yy=rand(n,1);% stopping errortol=1e-8;% max number of iterationsmax_steps=5000;% power method algorithmerr=10*tol;steps=0;z=A*y;v=dot(y,z)/dot(y,y);while err>tol    steps=steps+1;    if steps > max_steps        error('Max number of steps reached')    end    vv=v;    y=z/norm(z,2);    z=A*y;    v=dot(y,z);        err=abs((v-vv)/v);endy=z/norm(z,2);